cell.eg:
element availability
{
   attribute status { "free" | "unknown" | "restricted" }?,
   ( model.availabilityPart | model.pLike )+
}

element byline
{
   ( text | model.gLike | model.phrase | docAuthor | model.global )*
}

element category
{
   ( catDesc+ | ( model.descLike | model.glossLike )* ), category*
}

