xCheckbox1.setSize(fCheckbox1Size);

cell.eg:
element correction
{
   attribute status { "high" | "medium" | "low" | "unknown" }?,
   attribute method { "silent" | "markup" }?,
   model.pLike+
}

element dateline
{
   ( text | model.gLike | model.phrase | model.global | docDate )*
}

element desc
{
   attribute type { "deprecationInfo" | xsd:Name }?,
   macro.limitedContent
}

element div
{
   ( model.divTop | model.global )*,
   (
      (
         ( ( model.divLike | model.divGenLike ), model.global* )+
       | (
            ( model.common, model.global* )+,
            ( ( model.divLike | model.divGenLike ), model.global* )*
         )
      ),
      ( model.divBottom, model.global* )*
   )?
}

element egXML
{
   attribute valid { "true" | "feasible" | "false" }?,
   ( text | anyElement-egXML )*
}

element event
{
   attribute where { text }?,
   (
      model.headLike*,
      ( model.pLike+ | model.labelLike+ ),
      ( model.noteLike | model.biblLike | linkGrp | link )*,
      event*
   )
}

element figure
{
   attribute type
   {
      "photograph" | "advertisement" | "illustration" | "paratext" | "ephemera"
   }?,
   head?,
   figDesc?,
   bibl?,
   listPerson?,
   graphic
}

element front
{
   ( model.frontPart | model.pLike | model.pLike.front | model.global )*,
   (
      (
         (
            model.div1Like,
            ( model.div1Like | model.frontPart | model.global )*
         )
       | ( model.divLike, ( model.divLike | model.frontPart | model.global )* )
      ),
      ( model.divBottom, ( model.divBottom | model.global )* )?
   )?
}

element gap
{
   attribute reason
   {
      list
      {
         (
            "cancelled"
          | "deleted"
          | "editorial"
          | "illegible"
          | "inaudible"
          | "irrelevant"
          | "sampling"
         )+
      }
   }?,
   attribute agent { text }?,
   ( model.descLike | model.certLike )*
}

element idno
{
   attribute type
   {
      "ISBN" | "ISSN" | "DOI" | "URI" | "VIAF" | "ESTC" | "OCLC"
   }?,
   ( text | model.gLike | idno )*
}

element l
{
   ( text | model.gLike | model.phrase | model.inter | model.global )*
}

element language
{
   attribute ident { text },
   attribute usage { text }?,
   macro.phraseSeq.limited
}

element list
{
   attribute type
   {
      "gloss" | "index" | "instructions" | "litany" | "syllogism"
   }?,
   (
      ( model.divTop | model.global )*,
      (
         ( item, model.global* )+
       | (
            headLabel?,
            headItem?,
            ( label, model.global*, item, model.global* )+
         )
      ),
      ( model.divBottom, model.global* )*
   )
}

element listBibl
{
   model.headLike*,
   ( model.biblLike | model.milestoneLike )+,
   ( relation | listRelation )*
}

element listEvent
{
   model.headLike*, model.eventLike+, ( relation | listRelation )*
}

element listOrg
{
   model.headLike*, ( org | listOrg )+, ( relation | listRelation )*
}

element listPerson
{
   model.headLike*,
   ( model.personLike | listPerson )+,
   ( relation | listRelation )*
}

element listPlace
{
   model.headLike*,
   ( model.placeLike | listPlace )+,
   ( relation | listRelation )*
}

element listRelation
{
   model.headLike*, ( model.pLike | ( relation | listRelation )+ )
}

element msIdentifier
{
   ( placeName?, institution?, repository?, collection*, idno* ),
   ( msName | objectName | altIdentifier )*
}

element num
{
   attribute type { "cardinal" | "ordinal" | "fraction" | "percentage" }?,
   attribute value { text }?,
   macro.phraseSeq
}

element place
{
   model.headLike*,
   (
      model.pLike*
    | ( model.labelLike | model.placeStateLike | model.eventLike )*
   ),
   ( model.noteLike | model.biblLike | idno | linkGrp | link )*,
   ( model.placeLike | listPlace )*
}

element punctuation
{
   attribute marks { "none" | "some" | "all" }?,
   attribute placement { "internal" | "external" }?,
   model.pLike*
}

element relatedItem
{
   attribute target { text }?,
   ( model.biblLike | model.ptrLike )?
}

element rendition
{
   attribute scope { text }?,
   attribute selector { text }?,
   macro.limitedContent
}

element table
{
   attribute rows { text }?,
   attribute cols { text }?,
   (
      ( model.headLike | model.global )*,
      ( ( row, model.global* )+ | ( model.graphicLike, model.global* )+ ),
      ( model.divBottom, model.global* )*
   )
}

element taxonomy
{
   (
      ( category | taxonomy )+
    | ( ( model.glossLike | model.descLike )+, ( category | taxonomy )* )
   )
 | ( model.biblLike, ( category | taxonomy )* )
}

element titlePage
{
   attribute type { text }?,
   (
      model.global*,
      model.titlepagePart,
      ( model.titlepagePart | model.global )*
   )
}

element unclear
{
   attribute reason
   {
      list
      {
         (
            "illegible"
          | "inaudible"
          | "faded"
          | "background_noise"
          | "eccentric_ductus"
         )+
      }
   }?,
   attribute agent { text }?,
   macro.paraContent
}

